    if let coord = selectedCoordinate {            return [MapAnnotationItem(coordinate: coord)]        }        return []    }}// Helper funkcija za konverziju točke na ekranu u koordinate (pojednostavljeno)func coordinateFrom(point: CGPoint, in region: MKCoordinateRegion) -> CLLocationCoordinate2D {    let span = region.span    let center = region.center    let mapWidth: CGFloat = UIScreen.main.bounds.width    let mapHeight: CGFloat = UIScreen.main.bounds.height    let xPercent = point.x / mapWidth    let yPercent = point.y / mapHeight    let latitude = center.latitude + (Double(yPercent - 0.5) * -span.latitudeDelta)    let longitude = center.longitude + (Double(xPercent - 0.5) * span.longitudeDelta)    return CLLocationCoordinate2D(latitude: latitude, longitude: longitude)}